import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { calculateQuizStats } from '../services/quizApi';
import './Results.css';

const Results = ({ userName, category, onRestart }) => {
  const [answers, setAnswers] = useState([]);
  const [questions, setQuestions] = useState([]);
  const [stats, setStats] = useState(null);
  const [showDetailedResults, setShowDetailedResults] = useState(false);
  const [animationStep, setAnimationStep] = useState(0);

  useEffect(() => {
    // Load quiz data from localStorage
    const savedAnswers = localStorage.getItem('quizAnswers');
    const savedQuestions = localStorage.getItem('quizQuestions');
    
    if (savedAnswers) {
      setAnswers(JSON.parse(savedAnswers));
    }
    if (savedQuestions) {
      setQuestions(JSON.parse(savedQuestions));
    }
  }, []);

  useEffect(() => {
    if (answers.length > 0 && questions.length > 0) {
      // Use setTimeout to prevent blocking the UI
      const timeoutId = setTimeout(() => {
        const quizStats = calculateQuizStats(questions, answers);
        setStats(quizStats);
      }, 100);
      
      return () => clearTimeout(timeoutId);
    }
  }, [answers, questions]);

  useEffect(() => {
    // Animate results step by step
    const timer = setTimeout(() => {
      setAnimationStep(prev => Math.min(prev + 1, 3));
    }, 500);
    
    return () => clearTimeout(timer);
  }, [animationStep]);

  const getPerformanceMessage = (percentage) => {
    if (percentage >= 90) return "Outstanding! You're a true expert! üèÜ";
    if (percentage >= 80) return "Excellent work! You really know your stuff! üåü";
    if (percentage >= 70) return "Great job! You have solid knowledge! üëç";
    if (percentage >= 60) return "Good effort! Keep learning and improving! üìö";
    if (percentage >= 50) return "Not bad! There's room for improvement! üí™";
    return "Keep studying! Every expert was once a beginner! üéØ";
  };

  const getPerformanceColor = (percentage) => {
    if (percentage >= 80) return '#4CAF50';
    if (percentage >= 60) return '#FF9800';
    return '#F44336';
  };

  const getGrade = (percentage) => {
    if (percentage >= 90) return 'A+';
    if (percentage >= 80) return 'A';
    if (percentage >= 70) return 'B';
    if (percentage >= 60) return 'C';
    if (percentage >= 50) return 'D';
    return 'F';
  };

  const handleRestart = () => {
    localStorage.removeItem('quizAnswers');
    localStorage.removeItem('quizQuestions');
    localStorage.removeItem('quizScore');
    onRestart();
  };

  if (!stats || !questions.length) {
    return (
      <div className="results-container">
        <div className="container">
          <div className="loading-state">
            <div className="spinner"></div>
            <h2>Loading your results...</h2>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="results-container">
      <div className="container fade-in">
        <div className="results-header">
          <div className="completion-celebration">
            <div className="trophy-icon">üèÜ</div>
            <h1>Quiz Complete!</h1>
            <p>Congratulations, {userName}!</p>
          </div>
        </div>

        <div className="results-summary">
          <div className="score-display">
            <div className="main-score">
              <span className="score-number">{stats.correctAnswers}</span>
              <span className="score-divider">/</span>
              <span className="score-total">{stats.totalQuestions}</span>
            </div>
            <div className="score-percentage">
              {stats.percentage}%
            </div>
            <div className="score-grade">
              Grade: {getGrade(stats.percentage)}
            </div>
          </div>

          <div className="performance-message">
            <p style={{ color: getPerformanceColor(stats.percentage) }}>
              {getPerformanceMessage(stats.percentage)}
            </p>
          </div>
        </div>

        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-icon">‚úÖ</div>
            <div className="stat-content">
              <div className="stat-number">{stats.correctAnswers}</div>
              <div className="stat-label">Correct Answers</div>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon">‚ùå</div>
            <div className="stat-content">
              <div className="stat-number">{stats.totalQuestions - stats.correctAnswers}</div>
              <div className="stat-label">Incorrect Answers</div>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon">‚è±Ô∏è</div>
            <div className="stat-content">
              <div className="stat-number">{Math.round(stats.averageTime)}s</div>
              <div className="stat-label">Average Time</div>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon">üéØ</div>
            <div className="stat-content">
              <div className="stat-number">{stats.accuracy}%</div>
              <div className="stat-label">Accuracy</div>
            </div>
          </div>
        </div>

        <div className="quiz-details">
          <h3>üìä Quiz Details</h3>
          <div className="details-grid">
            <div className="detail-item">
              <span className="detail-label">Category:</span>
              <span className="detail-value">{category.name}</span>
            </div>
            <div className="detail-item">
              <span className="detail-label">Difficulty:</span>
              <span className="detail-value">{category.difficulty}</span>
            </div>
            <div className="detail-item">
              <span className="detail-label">Questions:</span>
              <span className="detail-value">{stats.totalQuestions}</span>
            </div>
            <div className="detail-item">
              <span className="detail-label">Time per question:</span>
              <span className="detail-value">30 seconds</span>
            </div>
          </div>
        </div>

        <div className="action-buttons">
          <button
            className="btn btn-primary"
            onClick={() => setShowDetailedResults(!showDetailedResults)}
          >
            {showDetailedResults ? 'Hide' : 'Show'} Detailed Results
          </button>
          
          <button
            className="btn btn-secondary"
            onClick={handleRestart}
          >
            üîÑ Take Another Quiz
          </button>
          
          <Link
            to="/general-knowledge"
            className="btn btn-outline"
          >
            Learn More üìö
          </Link>
        </div>

        {showDetailedResults && (
          <div className="detailed-results">
            <h3>üìù Question-by-Question Breakdown</h3>
            <div className="questions-list">
              {questions.map((question, index) => {
                const answer = answers[index];
                const isCorrect = answer?.isCorrect || false;
                const selectedAnswer = answer?.selectedAnswer || 'No answer';
                
                return (
                  <div key={index} className={`question-result ${isCorrect ? 'correct' : 'incorrect'}`}>
                    <div className="question-header">
                      <div className="question-number">Q{index + 1}</div>
                      <div className="question-status">
                        {isCorrect ? '‚úÖ Correct' : '‚ùå Incorrect'}
                      </div>
                    </div>
                    
                    <div className="question-text">
                      {question.question}
                    </div>
                    
                    <div className="answer-details">
                      <div className="answer-row">
                        <span className="answer-label">Your answer:</span>
                        <span className={`answer-value ${isCorrect ? 'correct' : 'incorrect'}`}>
                          {selectedAnswer}
                        </span>
                      </div>
                      
                      {!isCorrect && (
                        <div className="answer-row">
                          <span className="answer-label">Correct answer:</span>
                          <span className="answer-value correct">
                            {question.correct_answer}
                          </span>
                        </div>
                      )}
                      
                      <div className="answer-row">
                        <span className="answer-label">Time spent:</span>
                        <span className="answer-value">
                          {answer?.timeSpent || 0} seconds
                        </span>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        )}

        <div className="share-section">
          <h3>üéâ Share Your Achievement</h3>
          <p>Proud of your score? Share it with friends!</p>
          <div className="share-buttons">
            <button
              className="btn btn-outline"
              onClick={() => {
                const text = `I just scored ${stats.percentage}% on the ${category.name} quiz! Can you beat my score? üéØ`;
                navigator.clipboard.writeText(text);
                alert('Score copied to clipboard!');
              }}
            >
              Copy Score üìã
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Results;
