import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import UsernameForm from './components/UsernameForm';
import CategorySelection from './components/CategorySelection';
import Quiz from './components/Quiz';
import Results from './components/Results';
import GeneralKnowledge from './components/GeneralKnowledge';
import { QuizProvider } from './context/QuizContext';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [category, setCategory] = useState(null);
  const [quizCompleted, setQuizCompleted] = useState(false);

  // Load user data from localStorage on mount
  useEffect(() => {
    const savedUser = localStorage.getItem('quizUser');
    const savedCategory = localStorage.getItem('quizCategory');
    const savedQuizCompleted = localStorage.getItem('quizCompleted');
    
    if (savedUser) setUser(JSON.parse(savedUser));
    if (savedCategory) setCategory(JSON.parse(savedCategory));
    if (savedQuizCompleted) setQuizCompleted(JSON.parse(savedQuizCompleted));
  }, []);

  const handleUserSubmit = (userData) => {
    setUser(userData);
    localStorage.setItem('quizUser', JSON.stringify(userData));
  };

  const handleCategorySelect = (selectedCategory) => {
    setCategory(selectedCategory);
    localStorage.setItem('quizCategory', JSON.stringify(selectedCategory));
  };

  const handleQuizComplete = () => {
    setQuizCompleted(true);
    localStorage.setItem('quizCompleted', JSON.stringify(true));
  };

  const handleRestart = () => {
    setUser(null);
    setCategory(null);
    setQuizCompleted(false);
    // Clear all quiz-related localStorage
    localStorage.removeItem('quizUser');
    localStorage.removeItem('quizCategory');
    localStorage.removeItem('quizCompleted');
    localStorage.removeItem('quizAnswers');
    localStorage.removeItem('quizScore');
    localStorage.removeItem('quizQuestions');
    // Force page reload to reset all state
    window.location.reload();
  };

  return (
    <QuizProvider>
      <Router>
        <div className="App">
          <Routes>
            <Route 
              path="/" 
              element={
                !user ? (
                  <UsernameForm onSubmit={handleUserSubmit} />
                ) : !category ? (
                  <CategorySelection 
                    onSelect={handleCategorySelect}
                    userName={user.name}
                  />
                ) : !quizCompleted ? (
                  <Quiz 
                    category={category}
                    userName={user.name}
                    onComplete={handleQuizComplete}
                  />
                ) : (
                  <Results 
                    userName={user.name}
                    category={category}
                    onRestart={handleRestart}
                  />
                )
              } 
            />
            <Route 
              path="/general-knowledge" 
              element={<GeneralKnowledge onBack={() => window.history.back()} />} 
            />
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </div>
      </Router>
    </QuizProvider>
  );
}

export default App;
